INSERT INTO `places` (`id`, `name`, `description`, `un`, `region_id`) VALUES
(1, 'Балахониха', 'Балахониха — посёлок в Арзамасском районе Нижегородской области. Располагается в 35 км к северу от Арзамаса.\r\nОснован в 1909 году при строительстве железнодорожной ветки Муром-Арзамас.\r\nВ конце 1950-х – начале 1960-х гг. в 2 км от поселка построена нефтеперекачивающая станция нефтепровода Альметьевск – Котово – Рязань с рабочей силой в 120 человек.  Поселок при нефтеперекачивающей станции вошел в состав поселка Балахониха. \r\nВ настоящее время в поселке Балахониха проживают 820 человек.\r\nИз них около 180 человек детей и молодежи.\r\nОсновные рабочие места: ж/д (50 чел.), станция Арзамас-2 (30 чел.), НПС «Балахониха» (79 чел.), социальная сфера.\r\n', 'balahoniha', 1),
(2, 'Кстово', 'административный центр Кстовского муниципального района Нижегородской области. Численность населения — 67 723 чел. \r\nОдно из градообразующих предприятий района — Горьковское районное нефтепроводное управление АО «Транснефть-Верхняя Волга».', 'kstovo', 1),
(3, 'Тумботино', 'посёлок городского типа Павловского муниципального района Нижегородской области, имеющего статус городского поселения. \r\nЧисленность населения — 6 855 чел.\r\nОдно из градообразующих предприятий района — нефтеперекачивающая станция (НПС) «Степаньково» Горьковского районного нефтепроводного управления АО «Транснефть-Верхняя Волга».', 'tumbotino', 1),
(4, 'Валки', 'село в Лысковском районе Нижегородской области, административный центр Валковского сельсовета. Численность населения — 722 человека. Одно из градообразующих предприятий района — нефтеперекачивающая станция (НПС)  «Макарьево» Марийского районного нефтепроводного управления АО «Транснефть-Верхняя Волга».', 'valki', 1),
(5, 'Воротынец', 'посёлок городского типа в Нижегородской области, административный центр Воротынского муниципального района. Образует городское поселение рабочий посёлок Воротынец. Численность населения - 7177 чел.\r\nОдно из градообразующих предприятий поселка — линейно-производственно-диспетчерская станция (ЛПДС) \"Воротынец\" Марийского районного нефтепроводного управления АО «Транснефть-Верхняя Волга».', 'vorotynets', 1),
(6, 'Ермишь', 'Ермишь  — посёлок городского типа, административный центр Ермишинского района Рязанской области.\r\nРасположен на берегу реки Ермишь, в 245 км к востоку от Рязани, в 65 км к северо-востоку от железнодорожной станции Сасово.\r\nЧисленность населения 3 765 человек.\r\nВ Ермиши действуют центр детского и юношеского творчества, детско-юношеская спортивная и музыкальная школы, дом культуры, библиотека. Издаётся газета «Ермишинский вестник». \r\nВ черте посёлка находится памятник природы — Ермишинский пруд. \r\nВблизи поселка расположена нефтепрекачивающая станция Ермишь Рязанского районного нефтепроводного управления.', 'ermish', 2),
(7, 'Мосолово', 'село в Шиловском районе Рязанской области, административный центр Мосоловского сельского поселения.\r\nЧисленность населения — 1740 человек.\r\nСело Мосолово Шиловского района Рязанской области расположено рядом со станцией «Шилово» Рязанского районного нефтепродуктопроводного управления.', 'mosolovo', 2),
(8, 'Достижение', 'посёлок в Ковровском районе Владимирской области, в 9,6 км от центра Коврова.\r\nРядом расположена нефтеперекачивающая станция «Филино» Горьковского районного нефтепроводного управления.\r\n', 'dostijenie', 6),
(9, 'Редькино', 'Село Редькино Нижегородской области находится в 52 км. от г. Нижнего Новгорода.\r\nСело входит в состав городского округа город Бор.\r\nЧисленность населения 1 423 человека.', 'redkino', 1),
(10, 'Филино', 'посёлок в Ковровском районе Владимирской области России, в 19 км на восток от райцентра города Ковров.\r\nЧисленность населения 173 человека.\r\nПосёлок возник в 1960-е годы в связи со строительством нефтеперекачивающей станции «Филино».', 'filino', 6),
(11, 'Камешково', '— город во Владимирской области России, административный центр Камешковского района. \r\nЧисленность населения — 12 496 человек.\r\nРядом с городом расположена промежуточно-перекачивающая станция «Второво» Горьковского районного нефтепроводного управления.', 'kameshkovo', 6),
(12, 'Великие Луки', ' — город в Псковской области, «Город воинской славы»; является городом областного подчинения.\r\nКрупный многопрофильный торгово-промышленный и культурно-образовательный центр юга Псковской области.\r\nЧисленность населения 90 549 человек.\r\nНа территории города располагается завод «Транснефтемаш» - филиал АО «Транснефть – Верхняя Волга».', 'luki', 13),
(13, 'Килемары', '— посёлок городского типа, административный центр Килемарского района Республики Марий Эл России. Является центром одноимённого городского поселения. \r\nЧисленность населения поселка 4 505 человек.\r\nРядом с поселком расположена нефтеперекачивающая станция «Килемары» Марийского районного нефтепроводного управления.', 'kilemari', 4),
(14, 'Киреевск', '— город Тульской области, административный центр Киреевского района и муниципального образования (городского поселения) «город Киреевск». \r\nНаселение 25 741 человек.\r\n', 'kireevsk', 10),
(15, 'Юрино', '— посёлок городского типа в России, административный центр Юринского района Республики Марий Эл. Население — 2984 человек. \r\nРядом с городом расположена нефтеперекачивающая станция «Юрино» Марийского районного нефтепроводного управления.\r\n', 'urino', 4),
(16, 'Константиново', '— село в Раменском районе Московской области, административный центр сельского поселения Константиновское.\r\nНаселение — 3 974 человека. \r\nНа территории села расположена линейно-производственная диспетчерская станция «Володарская» Володарского районного нефтепродуктопроводного управления.\r\n', 'konstantinovo', 3),
(17, 'Залесье', 'Деревня Залесье — Ивановский район Ивановской области.', 'zalese', 7),
(18, 'Сернур', 'поселок городского типа, административный центр Сернурского района республики Марий Эл. Население - 8300 человек.', 'sernur', 4),
(19, 'Шилово', '— посёлок городского типа, административный центр Шиловского городского поселения и Шиловского муниципального района Рязанской области.\r\nЧисленность населения —14 531 человек.\r\nВблизи поселка расположена головная перекачивающая станция «Шилово-3» Рязанского районного нефтепроводного управления.\r\n', 'shilovo', 2);

