INSERT INTO `projects` (`id`, `name`, `description`, `period`, `costs`, `people`, `b_directory`, `a_directory`, `video`, `type_id`, `place_id`) VALUES
(3, 'Реконструкция МБОУ основная общеобразовательная школа п. Балахониха ', 'Здание школы построено в 1980г.\r\nВ настоящее время в учреждении обучается 42 чел., в том числе 30 детей работников НПС «Балахониха» Горьковского РНУ.\r\nШкола является базовой для удаленных сельских поселений, в том числе поселка Балахониха.\r\nИз 85 работников НПС «Балахониха» 70  человек в обучались в данной школе.', 'Март - Август 2014 года', 30.00, '42 учащихся', 'sch/b', 'sch/a', 'school.mp4', 1, 1),
(4, 'Капитальный ремонт МКУ «Центр комплексного обслуживания «Культура»', 'Здание дома культуры построено 1967 году.\r\nУчреждение обслуживает удаленные сельские поселения, всего около 2 200 чел.\r\nВ доме культуры созданы театральные, вокальные, танцевальные, декоративно-прикладного творчества коллективы. Ежегодно проводится более 200 мероприятий различной направленности, большая часть для детей и молодежи. \r\nВ здании расположена сельская библиотека. Книжный фонд насчитывает 7700 экз. \r\n', 'Май 2017 - Июнь 2018', 14.00, '2200 человек', 'cul/b', 'cul/a', 'balahoniha_dk.mp4', 2, 1),
(5, 'Реконструкция МБДОУ Балахонихинский детский сад ', 'Здание детского сада построено в 1977 году.\r\nВ настоящее время  учреждение посещает 35 детей, в том числе 15 детей работников НПС «Балахониха» Горьковского РНУ.\r\nВ детском саду 2 группы: младшая —14 воспитанников (возраст 1,5 – 4 года), старшая — 21 воспитанник (возраст 4-7 лет).\r\n', 'Март - Август 2014 года', 20.00, '35 детей', 'ds/b', 'ds/a', 'child.mp4', 3, 1),
(6, 'Ремонт и оснащение Балахонинской врачебной амбулатории ', 'Здание амбулатории построено в 1978 году. Закреплен участок из 6 населенных пунктов с населением более 950 человек, из них 517 человек трудоспособного возраста. В амбулатории осуществляется прием взрослого и детского населения, оказываются стоматологические, гинекологические услуги. Дополнительно установлены электрокардиограф и стоматологическая установка.', 'Июнь - сентябрь 2015 года', 1.00, '950 человек', 'hos/b', 'hos/a', '', 4, 1),
(7, 'Ремонт и оснащение МБОУ СРЕДНЯЯ ШКОЛА № 3 Кстовского муниципального района', 'Здание построено в 1973 году.\r\nОсуществлено материально-технического оснащение кабинетов физики, математики, химии лабораторным и демонстрационным оборудованием. Проведен ремонт кабинетов физики, химии и математики.\r\n', '2017-2018', 4.00, '1294 обучающихся', 'sch3/b', 'sch3/a', '', 1, 2),
(8, 'Ремонт и оснащение МБОУ СРЕДНЯЯ ШКОЛА № 8 Кстовского муниципального района', 'Здание построено в 1992 году.\r\nОсуществлено материально-технического оснащение кабинетов физики, математики, химии лабораторным и демонстрационным оборудованием. Проведен ремонт кабинетов физики, химии и математики.', '2017-2018', 5.00, '1101 учащийся', 'sch8/b', 'sch8/a', '', 1, 2),
(9, 'Капитальные ремонт и оснащение МБОУ Гимназия №4 Кстовского муниципального района', 'Образовательный процесс организован в двух зданиях: основное здание  (год постройки - 1957г.); 2 здание (мастерские) (год постройки – 1962г.).', 'Июнь - Август 2016', 74.00, '711 учащихся', 'gim4/b', 'gim4/a', '', 1, 2),
(10, 'Капитальный ремонт МБОУ средняя школа №2 р.п. Тумботино', 'Здание школы построено 1977 году.\r\nВ 2013 г. в школе был проведен капитальный ремонт, в результате которого школы приобрела современный вид, новое оборудование и оснащение - беговую дорожку, хоккейную, волейбольную площадку, спортивный городок. В школе имеются кабинет психолога, стоматологический кабинет, медицинский и процедурный кабинеты,  столярная и слесарная мастерские, спортивный и актовый зал.', 'Июнь - Август 2013 года', 60.00, '552  учащихся', 'sch2/b', 'sch2/a', '', 1, 3),
(11, 'Строительство нового детского сада в с. Валки Лысковского муниципального района', 'Здание Валковского детского сада «Родничок» построено в 2015 году.\r\nМатериально-техническое обеспечение: младшая и старшая группы, физкультурный зал, музыкальный зал, методический кабинет. Общая площадь 317 кв.м.\r\nВсе прогулочные участки оснащены необходимым сертифицированным оборудованием: песочницы, скамейки, балансиры, игровое оборудование.\r\nПроектная мощность детского сада - 50 детей.\r\n', 'Июнь - Август 2015 года', 40.00, '35 воспитанников', 'ds/b', 'ds/a', '', 3, 4),
(12, 'Строительство нового водозабора п. Воротынец', 'Строительство нового водозабора для обеспечения холодной водой жителей п. Воротынец', 'Сентябрь - Октябрь 2014 года', 2.00, 'Численность населения — 7 177 человек', 'vodo/b', 'vodo/a', '', 5, 5),
(13, 'МБОУ Воротынская средняя общеобразовательная школа ', 'Выполнен ремонт кровли, замена систем отопления, утепление фасадов, замена оконных блоков, ремонт систем водоснабжения и канализации, вентиляции, замена напольного покрытия, ремонт помещений. В здании школы проведена перепланировка спортивного и актового залов, мастерских для уроков технологии, столовой, медицинского кабинета, санузлов. Выполнены работы по благоустройству территории, организованы пространства для малого спортивного зала и конференц-зала. Учреждение оснащено новейшим учебным оборудованием и мебелью.\r\nЗдание №1 - 1968 года постройки на 560 учащихся. Здание №2 - 1972 года постройки на 320 учащихся.\r\nКапитальный ремонт зданий с момента ввода в эксплуатацию не проводился.', 'февраль - август 2015 года', 65.00, '703 учащихся', 'sch/b', 'sch/a', '', 1, 5),
(14, 'Ермишинская средняя образовательная школа', 'Капитальный ремонт здания муниципального образовательного учреждения Ермишинская средняя образовательная школа и приобретения оборудования.\r\nВ школе обучаются дети, проживающие в р.п. Ермишь и близлежащих населенных пунктах Ермишинского района. В интернате при школе проживает до 20 учащихся. В распоряжении школы три здания: корпус школы, где проводятся занятия (1963 года постройки, общей площадью 4979,4 м2, 3 этажа), корпус начальной школы, где размещены 4 учебных класса для учащихся первых классов и столовая (1998 года постройки, общей площадью 224,8 м2, 2 этажа),  корпус интерната (1983 года постройки, общей площадью 491,5 м1, 2 этажа). В основном здании школы с момента ввода в эксплуатацию ремонт не проводился\r\nВыполнен ремонт кровли, замена систем отопления, утепление фасадов, замена оконных блоков, ремонт систем водоснабжения и канализации, вентиляции, замена напольного покрытия, ремонт помещений. В здании школы проведена перепланировка спортивного и актового залов, мастерских для уроков технологии, столовой, медицинского кабинета, санузлов. Выполнены работы по благоустройству территории, организованы пространства для малого спортивного зала и конференц-зала. Учреждение оснащено новейшим учебным оборудованием и мебелью.', 'март – август 2014 года', 35.00, '505 учащихся', 'sch/b', 'sch/a', '', 1, 6),
(15, 'Ермишинский детский сад', 'Капитальный ремонт здания муниципального дошкольного образовательного учреждения Ермишинский детский сад №3 и приобретение оборудования.\r\nПлощадь здания МДОУ № 1146,2(кв.м.).\r\nВ детском саду 6 групповых комнат,  музыкальный зал(спортивный зал), кабинет педагога-психолога, методический кабинет, кабинет учителя-логопеда, пищеблок, прачечная, медицинский кабинет. \r\nВыполнен ремонт кровли, замена систем отопления, утепление фасадов, замена оконных блоков, ремонт систем водоснабжения и канализации, вентиляции, замена напольного покрытия, ремонт помещений.', 'март – август 2014 года', 25.00, '105 детей', 'ds/b', 'ds/a', '', 3, 6),
(16, 'Водоснабжение р.п. Ермишь', 'Выполнение работ по переводу пос. Ермишь на водоснабжение от собственных источников (артезианских скважин). \r\nРеконструкция водопроводной сети по ул.Нефтяников, ул.Весенняя, ул.Новоселов в р.п. Ермишь Рязанской области', 'Май - Август 2016 года', 6.00, '3 765 человек', 'vodo/b', 'vodo/a', '', 5, 6),
(17, 'Транснефтьклассы в Ермишинской средней школе', 'Реализация корпоративной программы развития школьного образования ПАО «Транснефть», рассчитанной на 2017-2019 годы. Цель программы - создание условий для качественной реализации учебного процесса и повышения уровня знаний учащихся в точных дисциплинах - физике, химии, математике.\r\nВ кабинетах заменены двери, электрика, отремонтированы потолочные покрытия, пол и стены, поставлено новое оборудование в лабораторных комнатах. На выделяемые средства также закуплена мебель, интерактивные доски, компьютерная техника, учебные пособия, мультимедийное оборудование. ', 'Июнь-август 2018', 5.00, '505 учащихся', 'sch2/b', 'sch2/a', '', 1, 6),
(18, 'Реконструкция МБОУ «Мосоловская СОШ» Шиловского района ', 'Здание школы построено в 1979 году.\r\nШкола функционирует 2 - х этажном типовом кирпичном здании на 220 мест с водопроводом, канализацией и центральным отоплением. \r\nВыполнен ремонт кровли, замена систем отопления, утепление фасадов, замена оконных блоков, ремонт систем водоснабжения и канализации, вентиляции, замена напольного покрытия, ремонт помещений. В здании школы проведена перепланировка спортивного и актового залов, мастерских для уроков технологии, столовой, медицинского кабинета, санузлов. Выполнены работы по благоустройству территории, организованы пространства для малого спортивного зала и конференц-зала. Учреждение оснащено новейшим учебным оборудованием и мебелью.', 'март - август 2017 года', 128.00, '163 учащихся', 'sch/b', 'sch/a', '', 1, 7),
(19, 'МБОУ «Редькинская основная общеобразовательная школа» ', 'Здание школы построено в 1979 году.\r\nШкола имеет центральное отопление, люминесцентное освещение, холодное водоснабжение, канализацию, столовую (68 мест), спортзал (1 шт.), оборудованные кабинеты (психологический, компьютерный, комбинированную мастерскую и другие), музей, актовый зал. Библиотека имеет в своем фонде 3271 экземпляров учебников, художественный фонд – 7211 экземпляров.\r\nВыполнен ремонт кровли, замена систем отопления, утепление фасадов, замена оконных блоков, ремонт систем водоснабжения и канализации, вентиляции, замена напольного покрытия, ремонт помещений. В здании школы проведена перепланировка спортивного и актового залов, мастерских для уроков технологии, столовой, медицинского кабинета, санузлов. Выполнены работы по благоустройству территории, организованы пространства для малого спортивного зала и конференц-зала. Учреждение оснащено новейшим учебным оборудованием и мебелью.', 'Июль-август 2012 года', 20.00, '139 учеников', 'sch/b', 'sch/a', '', 1, 9),
(20, 'Реконструкция МБОУ «Средняя общеобразовательная школа №1»', 'Здание школы построено в 1989 году, четырехэтажное, кирпичное, с двухэтажной пристройкой.\r\nОбщая площадь здания – 9055,4 м2.\r\nОтопление от индивидуальной автономной газовой котельной, водоснабжение и канализация централизованные. \r\nПлощадь школьной территории составляет 29082 м2. \r\nВ школе имеется паспортизированный музей, силами педагогов и детей создан Зимний сад. На территории школы расположено открытое спортивное сооружение, в цокольном этаже оборудован школьный тир, тренажерный зал.\r\nВ 2005 году, на собранные жителями города средства,  был установлен памятник погибшим в годы Великой отечественной войны выпускникам школы. \r\nВыполнен ремонт кровли, замена систем отопления, утепление фасадов, замена оконных блоков, ремонт систем водоснабжения и канализации, вентиляции, замена напольного покрытия, ремонт помещений. В здании школы проведена перепланировка спортивного и актового залов, мастерских для уроков технологии, столовой, медицинского кабинета, санузлов. Выполнены работы по благоустройству территории, организованы пространства для малого спортивного зала и конференц-зала. Учреждение оснащено новейшим учебным оборудованием и мебелью.', 'январь –декабрь 2018', 154.00, '862 ученика', 'sch/b', 'sch/a', 'kameshkovo_school.mp4', 1, 11),
(21, 'Капитальный ремонт и приобретение оборудования МБОУ «Осиповская СОШ им. Т.Ф.Осиповского», ', 'Школа построена в 1978 г.\r\nШкола является базовой для удаленных сельских поселений, в том числе поселка НПС «Филино».\r\nВ школе с 1978 г. не проводилось капитального ремонта, все системы (водоснабжение, канализация, освещение).\r\nВыполнен ремонт кровли, замена систем отопления, утепление фасадов, замена оконных блоков, ремонт систем водоснабжения и канализации, вентиляции, замена напольного покрытия, ремонт помещений. В здании школы проведена перепланировка спортивного и актового залов, мастерских для уроков технологии, столовой, медицинского кабинета, санузлов. Выполнены работы по благоустройству территории, организованы пространства для малого спортивного зала и конференц-зала. Учреждение оснащено новейшим учебным оборудованием и мебелью.\r\n', 'май-август 2015 года', 50.00, '190 человек', 'sch/b', 'sch/a', '', 1, 8),
